import React, { useState } from 'react';
import { useLoginMutation } from '../api/authApi'; // Login API hook
import { useDispatch } from 'react-redux';
import { setCredentials } from '../slices/authSlice';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [login, { isLoading }] = useLoginMutation();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const { token } = await login({ email, password }).unwrap();  // Call login API and get token
      dispatch(setCredentials({ token, user: { email } }));          // Store token and user in Redux

      // Save token to localStorage (optional, for persistence)
      localStorage.setItem('token', token);

      // Redirect to homepage
      navigate('/');
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
      />
      <button type="submit" disabled={isLoading}>
        Login
      </button>
    </form>
  );
};

export default Login;
