import React, { useState } from 'react';
import { useLoginMutation } from '../store/apis/authApi'; // Login API hook
import { useDispatch } from 'react-redux';
import { setCredentials } from '../store/slices/authSlice';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [login, { isLoading }] = useLoginMutation();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const { token, user } = await login({ email, password }).unwrap(); // Make sure the response includes `user`
      console.log('Received token:', token);
      console.log('User ID:', user.id); // Log user ID to check
      dispatch(setCredentials({ token, userId: user.id })); // Store userId
      localStorage.setItem('token', token); // Store the token in localStorage
      navigate('/');
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
      />
      <button type="submit" disabled={isLoading}>
        Login
      </button>
    </form>
  );
};

export default Login;
