import React, { useEffect, useState } from 'react';
// import { useSelector, useDispatch } from'react-redux';
import { useFetchTodosQuery } from '../store/apis/todosApi';
import axios from 'axios';
import TodoItem from './TodoItem';
import { Typography, CircularProgress } from '@mui/material';

const TodoList = () => {
    const [data: todos, error, isLoading] = useFetchTodosQuery()
    // const [todos, setTodos] = useState([]);
    // const [loading, setLoading] = useState(true);


    // useEffect(() => {
    //     fetchTodos();
    // }, []);

    // const fetchTodos = async () => {
    //     try {
    //         const response = await axios.get('https://todo-list-backend-nvp1.onrender.com/api/todos');
    //         setTodos(response.data);
    //         setLoading(false);

    //     } catch (error) {
    //         console.error('Error fetching todos:', error);
    //     }
    // };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`https://todo-list-backend-nvp1.onrender.com/api/todos/${id}`);
            setTodos(todos.filter(todo => todo.id !== id));
        } catch (error) {
            console.error('Error deleting todo:', error);
        }
    };


    if (isLoading) {
        return <CircularProgress />;
    }
    if (todos.length === 0) {
        return <Typography variant="h6">No todos available. Add some!</Typography>;
    }

    return (
        <div>
            <h2>Todo List</h2>
            <ul>
                {todos.map(todo => (
                    <TodoItem key={todo.id} todo={todo} onDelete={handleDelete} />
                ))}
            </ul>
        </div>
    );
};

export default TodoList;
